function logData (filename, dataToBeLogged) {
	var fs = require('fs');
	fs.readFile(filename, 'utf8', function (err,data) {
	  if (err) {
	    return console.log(err);
	  }	
	  fs.writeFile(filename, data + dataToBeLogged, function(err) {
		    if(err) {
		        console.log(err);
		    } 
		}); 
	});

}